import jsonimport redef affichage ():    print(        "\n \n                      ****** vous avez 5choix a faire *******\n"        "                        ðŸ‘‰ 1 . c'est pour ajouter un contact\n"        "                        ðŸ‘‰ 2 . c'est pour afficher un contact \n"        "                        ðŸ‘‰ 3 . c'est POUR modifier UN CONTACT \n"        "                        ðŸ‘‰ 4 . c'est pour supprimer \n"        "                        ðŸ‘‰ 5 . pour EXIT \n")def ajout(id,nom,pre,adr,tel,mel):    fichier = "gestion.json"    with open(fichier, "r") as f:        se = json.load(f)        gen={}        gen.update({id+"nom": nom})        gen.update({id+"pre": pre})        gen.update({id+"adr": adr})        gen.update({id+"tel": tel})        gen.update({id+"mel": mel})        se[id]=gen        with open(fichier, "w") as f:            json.dump(se, f, indent=4)            print("\n âœ…  BRAVO !!!  votre contacte est bien ajoutÃ© \n")        print("                       âœ… id generale :"+id+"\n    ")        print("                       âœ…  id 1 :   "+id+"nom --> pour "+nom+"\n")        print("                       âœ…  id 2 :   "+id+"pre --> pour "+pre+"\n")        print("                       âœ… id 3 :   "+id+"adr --> pour "+adr+"\n")        print("                       âœ…  id 4 :   "+id+"tel --> pour "+tel+"\n")        print("                       âœ…  id 5 :   "+id+"mel --> pour "+mel+"\n")        print( "veuillez memoriser les id de chaque information\n"          "pour pouvoir les modifier ou supprimer ")    affichage()def afficher(tel):    fichier = open('gestion.json','r')    b=json.load(fichier)    print("voulez vous afficher toutes les informations o/n ?")    z=input()    if z=="o":     print("\n==========â¬‡â¬‡â¬‡voici les informations de votre contactâ¬‡â¬‡â¬‡======\n")     print(b[tel][tel+"nom"])     print(b[tel][tel + "pre"])     print(b[tel][tel + "adr"])     print(b[tel][tel + "tel"])     print(b[tel][tel + "mel"])    elif z=="n" :      print(" saisissez l'ID de l'information que vous souhaitez afficher \n"            "pour le nom tapez:",[tel+"nom"],"\n"            "pour le prenom tapez:",[tel+"prenom"],"\n"            "pour l'adresse tapez:",[tel+"adr"],"\n"            "pour le tel tapez :",[tel+"tel"],"\n"            "pour le mail tapez:",[tel+"mel"],"\n"            )      f=input()      print("\n ðŸ˜‰==========â¬‡â¬‡â¬‡voici les informations de votre contactâ¬‡â¬‡â¬‡======ðŸ˜‰\n")      if f==tel+"nom" :       print(b[tel][tel+"nom"])      if f == tel + "pre":       print(b[tel][tel+"pre"])      if f == tel + "adr":       print(b[tel][tel+"adr"])      if f == tel + "tel":       print(b[tel][tel+"tel"])      if f == tel + "mel":       print(b[tel][tel+"mel"])def modifier(information,dic,modification):    fichier = "gestion.json"    with open(fichier, "r") as f:        se = json.load(f)    se[information][dic] = modification    with open(fichier, "w") as f:        json.dump(se, f, indent=4)        print(" ðŸ˜‰BRAVO!!!! votre information est bien modifiÃ©ðŸ˜‰")def supprimer(cle):    fichier = "gestion.json"    with open(fichier, "r") as f:        se = json.load(f)        print("voulez vous supprimer toutes les informations du contact o/n ")        z = input()        if z == "o":            del se[cle]        elif z == "n":            print("saisissez l ID de l'information Ã  supprimer :\n"                         "pour le nom tapez:",[cle + "nom"],"\n"                  "pour le prenom tapez:",[cle + "pre"],"\n"                  "pour l'adresse tapez:",[cle + "adr"], "\n"                  "pour le tel tapez :", [cle + "tel"], "\n"                  "pour le mail tapez:", [cle + "mel"], "\n"                  )            f = input()            if f == cle + "nom":                del se[cle][cle + "nom"]            if f == cle + "pre":                del se[cle][cle + "pre"]            if f == cle + "adr":                del se[cle][cle + "adr"]            if f == cle + "tel":                del se[cle][cle + "tel"]            if f == cle + "mel":                del se[cle][cle + "mel"]    with open(fichier, 'w') as f:        json.dump(se, f, indent=4)print("\n              =========================================")print("                ðŸ˜Ž ***systeme de gestion des contactes*** ðŸ˜Ž")print("               =========================================")affichage()while(1) : saisi=input("saisissez votre choix ðŸ‘‰ :") if saisi =="1" :    print("saisisser un identifiant pour ce contact")    inf=(input())    print("maintenant saisissez son nom ")    nom=(input())    print("maintenant saisissez son prenom")    pre = (input())    print("maintenant saisissez son adress")    adr = (input())    print("maintenant saisissez son tel")    tel = (input())    print("maintenant saisissez son mel")    mel = (input())    ajout(''+inf+'', ''+nom+'', ''+pre+'', ''+adr+'', ''+tel+'', ''+mel+'') elif saisi=="2" :      print("merci de saisir le ID generale de ce contacte ")      tel=input()      afficher(tel)      print("\n==========âž²âž²âž²âž²âž²âž²âž²âž²âž²===========\n\n")      affichage() elif saisi=="3":    print("saisissez lidentifiant du contact concernÃ©")    id=input()    print("saisissez lid de l'information que vous souhaitez modifier :\n"            "pour le nom tapez:",[id+"nom"],"\n"            "pour le prenom tapez:",[id+"pre"],"\n"            "pour l'adresse tapez:",[id+"adr"],"\n"            "pour le tel tapez :",[id+"tel"],"\n"            "pour le mail tapez:",[id+"mel"],"\n")    dic=input()    print("maintenant saisissez la modification que vous souhaiter faire")    mod=input()    modifier(id,dic,mod)    affichage() elif saisi=="4":     print("entrer lid GENERAL DU CONTACT concerne ")     sup=input()     supprimer(sup)     print ('ðŸ˜‰***cette information  est supprimer*****ðŸ˜‰\n')     affichage() elif saisi =="5" :    print('\n \n                ðŸ‘‹ðŸ‘‹    *** AU REVOIR ;) *****ðŸ‘‹ðŸ‘‹\n'          '       ***MERCI DAVOIR UTILISER LA "GESTION DES CONTACTES" ***')    break